										else null
									end,	
				entitytagmodifieddate=now(),
				entitytagmodifiedbyuuid = (select workerinstanceuuid from workerinstance where workerinstanceid = update_modifiedbyid)
		WHERE entitytaguuid = update_entitytaguuid;
end if;

End;

